#idLoginHtml{margin:0;padding:15px 15px 10px 15px}#idLoginPage{margin:0;padding:0;background:#F1F1F1;overflow:hidden;min-width:0;color:#1B1B1B;font-size:12px}#idLoginPage a,#idLoginPage a:active,#idLoginPage a:visited{color:#00a3de;text-decoration:none}#idLoginPage a:hover{color:#00a3de;text-decoration:underline}#idLoginPage .error{margin-bottom:10px;padding:0;color:#D30000;border:0;background:transparent;font-weight:bold;text-align:center}#idLoginPage.ie7 .error{line-height:18px}#idLoginForm{margin:0 auto;max-width:310px}#idLoginForm .tr .td{width:10%;white-space:nowrap;line-height:20px;padding-right:10px;padding-bottom:10px;font-weight:bold}#idLoginForm .tr .td + .td{padding-right:0;width:90%}#idLoginForm .login-fieldset input{width:91%;min-width:175px;color:#1B1B1B;background:#FFF;border-color:#B1B1B1;font-weight:normal;-moz-border-radius:1px;-webkit-border-radius:1px;border-radius:1px}#idLoginPage .login-fieldset input[class*=badlogin]{background:#FFE0E0 none 0 0;border-color:#C81919;border-width:2px;height:16px;width:89%;}#idLoginForm .login-fieldset input:focus{outline:0}#idLoginForm .secondary-controls{margin-top:5px;width:40%;font-size:11px}#idLoginForm .secondary-controls input{vertical-align:bottom;margin:0 3px 0 0}#idLoginForm #idLostPasswordLink{display:block;line-height:14px;margin-top:5px;margin-bottom:5px}#idLoginForm .primary-controls{margin-top:5px;text-align:center}#idLoginForm .primary-controls a{font-weight:bold}#idLoginForm #idLoginBtn{margin:0 0 10px;display:block;min-width:0;padding-left:14px;padding-right:14px}#idLoginRedirectHtml,#idLoginRedirectHtml body{height:100%}#idLoginPage .redirectMessage-wrapper{height:100%;position:relative}#idLoginPage #redirectMessage{position:absolute;left:0;top:50%;text-align:center;margin-top:-7px;width:100%}